function dijkstra(graph, source) {
  let dist = Array(graph.length).fill(Infinity);
  dist[source] = 0;
  let visited = new Set();
  let pq = new MinPriorityQueue();
  pq.enqueue(source, 0);
  while (!pq.isEmpty()) {
    let { element: u } = pq.dequeue();
    visited.add(u);
    for (let [v, weight] of graph[u]) {
      if (!visited.has(v) && dist[u] + weight < dist[v]) {
        dist[v] = dist[u] + weight;
        pq.enqueue(v, dist[v]);
      }
    }
  }
  return dist;
}

function prim(graph) {
  let mst = [];
  let visited = new Set();
  let pq = new MinPriorityQueue();
  pq.enqueue(0, 0);
  while (!pq.isEmpty()) {
    let { element: u } = pq.dequeue();
    if (visited.has(u)) continue;
    visited.add(u);
    for (let [v, weight] of graph[u]) {
      if (!visited.has(v)) pq.enqueue(v, weight);
    }
