  }
  return mst;
}

function kruskal(graph) {
  let mst = [];
  let ds = new DisjointSet();
  let edges = graph.getAllEdges().sort((a, b) => a.weight - b.weight);
  for (let edge of edges) {
    if (ds.find(edge.u) !== ds.find(edge.v)) {
      mst.push(edge);
      ds.union(edge.u, edge.v);
    }
  }
  return mst;
}

function preorder(node) {
  if (!node) return;
  visit(node);
  preorder(node.left);
  preorder(node.right);
}
function inorder(node) {
  if (!node) return;
  inorder(node.left);
  visit(node);
  inorder(node.right);
}
function postorder(node) {
  if (!node) return;
  postorder(node.left);
  postorder(node.right);
  visit(node);
}

class Node {
  constructor(value) {
    this.value = value;
    this.left = null;
    this.right = null;
  }
}

class BST {
  constructor() {
    this.root = null;
  }

  insert(value) {
    if (!this.root) {
      this.root = new Node(value);
      return;
    }
    let current = this.root;
    while (true) {
      if (value < current.value) {
        if (!current.left) {
          current.left = new Node(value);
          break;
        }
        current = current.left;
      } else {
        if (!current.right) {
          current.right = new Node(value);
          break;
        }
        current = current.right;
      }
    }
  }

  search(value) {
    let current = this.root;
    while (current) {
      if (value === current.value) return true;
      current = value < current.value ? current.left : current.right;
    }
    return false;
  }
}
